    /**
     *
     * @param array $trans
     * @return BankCapital|null
     */
    public static function insert($trans)
    {
        $bankOrder = new BankCapital();
        $bankOrder->order_id = $trans['JournalNo'];
        $bankOrder->bank_account = $trans['Account'];
        $bankOrder->order_p = $trans['TxnDesc'];
        $bankOrder->order_type = $trans['TxnType'];
        $bankOrder->order_amount = $trans['Amount'];
        $bankOrder->order_date = $trans['TxnDate'];
        $bankOrder->order_s = $trans['Branch'];
        $bankOrder->status = self::STAT_NEW;
        $bankOrder->vendor_id = VendorTable::BANK_CAPITAL;
        $bankOrder->created_at = date('Y-m-d H:i:s');
        $bankOrder->related_account = 0;
        $txnDesc = preg_replace("/\([0-9]{8}\)/", "", $trans['TxnDesc']);

        preg_match_all("/([9][954][0-9]{6})|(85[0-9]{6})/", $txnDesc, $matches);
        foreach ($matches as $numberArr) {
            foreach ($numberArr as $number) {
                $number = trim($number);
                if ($number) {
                    $bankOrder->order_mobile = $number;
                    $bankOrder->charge_mobile = $number;
                }
                continue;
            }
        }
        $bankOrder->save();
        return $bankOrder;
    }

    /**
     * Хуулга татах хаан
     *
     * @return boolean
     */
    public static function init()
    {
        $getSuccess = 0;
        $setSuccess = 0;
        $errorCode = -1;
        $desc = 'None';
        $request = $url = self::URL;
        $transactions = array();
        $response = '-';
        try {
            $client = new SoapClient($url);
            $obj = $client->Pay();
            $arr = self::object_to_array($obj);
            $errorCode = 0;
            $getSuccess = 1;
            $response = print_r($arr, true);
            $mobicomDeallerTransXml = $arr['PayResult'];
            $xml = simplexml_load_string($mobicomDeallerTransXml);
            $xmlArr = json_decode(json_encode((array) $xml), 1);
            $transactions = $xmlArr['txn'];
        } catch (SoapFault $e) {
            $desc = $e->faultstring;
            $responseSet = mysql_escape_string(print_r($desc, true));
        }
        if (count($transactions)) {
            $response = print_r($transactions, true);
            if (!isset($transactions[0])) {
                $temp[0] = $transactions;
                $transactions = $temp;
            }
            foreach ($transactions as $trans) {
                $param = array();
                $param['TxnType'] = $trans['type'];
                $param['Account'] = $trans['acct'];
                $param['JournalNo'] = $trans['jrnl'];
                $param['TxnDesc'] = AppTools::cp1251_utf8($trans['desc']);
                $param['Amount'] = (double) $trans['amt'];
                $param['Branch'] = $trans['br'];
                $param['TxnDate'] = date('Y-m-d H:i:s', strtotime($trans['date']));

                try {
                    $bankOrder = BankCapitalTable::insert($param);
                    // татсан тэмдэглэгээ хийх
                    $transIds[] = trim($param['JournalNo']);
                } catch (\Exception $exc) {
                    $logger = new sfFileLogger(new sfEventDispatcher(), array('file' => sfConfig::get('sf_log_dir') . '/my-capital-order.log'));
                    $logger->log('--ERROR--=' . trim($param['JournalNo']) . $exc->getTraceAsString(), sfFileLogger::INFO);
                }
                unset($trans);
            }
            if (count($transIds)) {
                BankCapitalTransTable::insert($bankOrder['id'], $transIds);
            }
        } else {
            $desc = "No transactions";
        }

        LogTools::setLogCapitalInit($request, $response, $responseSet, $errorCode, $desc, $getSuccess, $setSuccess);
        unset($response);
        unset($responseSet);
        return TRUE;
    }

