    /**
     * Дахин цэнэглэлт
     *
     * @param null $bankOrder
     * @param int $limit
     * @return boolean
     * @throws Doctrine_Manager_Exception
     * @throws Doctrine_Query_Exception
     */
    public static function recharge($bankOrder = null, $limit = 50)
    {
        $customer = FALSE;
        if ($bankOrder instanceof BankCapital) {
            if ($bankOrder->canReCharge()) {
                $bankOrder->status = self::STAT_PROCESS;
                $bankOrder->save();
                $customer = TRUE;
            }
            $bankOrderRows = array($bankOrder);
        } elseif ($bankOrder) {
            return FALSE;
        } else {
            $bankOrderRows = self::updateForCharge(array(BankCapitalAccountTable::ACCOUNT_DEALER, BankCapitalAccountTable::ACCOUNT_DEALER_MOBICOM), $limit);
        }

        foreach ($bankOrderRows as $bankOrder) {
            try {
                $SD = $ADshop = FALSE;
                $chargeResult = $adCode = null;
                if (in_array($bankOrder->status, array(self::STAT_FAILED_CHARGE, self::STAT_FAILED))) {
                    $bankOrder->status = self::STAT_NEW;
                    $bankOrder->save();
                }
                if ($bankOrder->status == self::STAT_NEW) {
                    $bankOrder->try_count++;
                    if ($bankOrder->charge_mobile != '') {
                        $dealer = DealerCharge::getDealer($bankOrder->charge_mobile);
                    } else {
                        $dealer = false;
                    }
                    if (BaseSms::isSdDealer($bankOrder->order_p)) {
                        $SD = TRUE;
                    } else {
                        $adCode = BaseSms::isAdShop($bankOrder->order_p);
                        if ($adCode) {
                            $ADshop = TRUE;
                        } else {
                            # If the charge amount exceeds the max amount limit
                            if ($bankOrder->order_amount > self::MAX_AMOUNT_LIMIT) {
                                $bankOrder->status = self::STAT_FAILED_MAX_AMOUNT;
                                $bankOrder->save();
                                self::notifyOperators($bankOrder);
                                continue;
                            } elseif ($bankOrder->order_amount < self::MIN_AMOUNT_LIMIT) {
                                $bankOrder->status = self::STAT_FAILED_MIN_AMOUNT;
                                $bankOrder->save();
                                self::notifyOperators($bankOrder);
                                continue;
                            }
                        }
                    }
                    if ($dealer || $adCode) {
                        $logId = LogTools::setLogCapitalCharge($bankOrder->id);

                        $time = -time();
                        # Цэнэглэлт хийх
                        if ($SD) {
                            $chargeResult = BaseSms::chargeSdDealer($bankOrder->charge_mobile, $bankOrder->order_amount, 'CAPITAL');
                        } elseif ($ADshop) {
                            $chargeResult = BaseSms::chargeAdDealer($adCode, $bankOrder->order_amount, 'CAPITAL');
                        } else {
                            $chargeResult = DealerCharge::charge($bankOrder->charge_mobile, $bankOrder->order_amount);
                        }
                        $time += time();

                        LogTools::updateLogCapitalCharge($logId, $chargeResult['log_request'], $chargeResult['log_response'], $time);

                        if ($chargeResult['success'] == TRUE) {
                            # Цэнэглэгдсэн мөнгөн дүн болон хувийг хадгалах
                            $bankOrder->charge_amount = $chargeResult['transferred'];
                            $bankOrder->percent = $chargeResult['percent'];
                            $bankOrder->save();
                            if ($customer) {
                                if ($ADshop) {
                                    TransactionTable::setRechargeAssignment(PaymentTypeTable::DEALER_AD, BankTable::CAPITAL, $bankOrder->bank_account, $bankOrder->order_id, $bankOrder->order_date, $bankOrder->order_p, $bankOrder->order_type, $bankOrder->order_amount, $bankOrder->order_s, $bankOrder->related_account);
                                } else {
                                    TransactionTable::setRechargeAssignment(PaymentTypeTable::DEALER, BankTable::CAPITAL, $bankOrder->bank_account, $bankOrder->order_id, $bankOrder->order_date, $bankOrder->order_p, $bankOrder->order_type, $bankOrder->order_amount, $bankOrder->order_s, $bankOrder->related_account);
                                }
                            } else {
                                try {
                                    if ($ADshop) {
                                        TransactionTable::setDealerAssignment(PaymentTypeTable::DEALER_AD, BankTable::CAPITAL, $bankOrder->bank_account, $bankOrder->order_id, $bankOrder->order_date, $bankOrder->order_p, $bankOrder->order_type, $bankOrder->order_amount, $bankOrder->order_s, $bankOrder->related_account);
                                    } else {
                                        TransactionTable::setDealerAssignment(PaymentTypeTable::DEALER, BankTable::CAPITAL, $bankOrder->bank_account, $bankOrder->order_id, $bankOrder->order_date, $bankOrder->order_p, $bankOrder->order_type, $bankOrder->order_amount, $bankOrder->order_s, $bankOrder->related_account);
                                    }
                                } catch (\Exception $e) {

                                }
                            }
                            if ($SD || $ADshop) {
                                $bankOrder->status = self::STAT_SUCCESS;
                                $bankOrder->save();
                            } else {
                                # Зарлага үүсгэх
                                $outcomeOrderId = self::reoutcome($bankOrder, $dealer);
                                if ($outcomeOrderId) {
                                    $bankOrder->status = self::STAT_SUCCESS;
                                    $bankOrder->sales_order_id = $outcomeOrderId;
                                    $bankOrder->save();
                                    continue;
                                } else {
                                    $bankOrder->status = self::STAT_FAILED_OUTCOME;
                                    $bankOrder->save();

                                    self::notifyOperators($bankOrder);
                                    continue;
                                }
                            }
                        } else {
                            if ($chargeResult['error_code'] == self::STAT_FAILED_MAX_AMOUNT) {
                                $bankOrder->status = self::STAT_FAILED_MAX_AMOUNT;
                            } elseif ($chargeResult['error_code'] == self::STAT_FAILED_MIN_AMOUNT) {
                                $bankOrder->status = self::STAT_FAILED_MIN_AMOUNT;
                            } else {
                                $bankOrder->status = self::STAT_FAILED_CHARGE;
                            }
                            $bankOrder->save();
                            self::notifyOperators($bankOrder);
                            continue;
                        }
                    } else {
                        $bankOrder->status = self::STAT_FAILED_DEALER;
                        $bankOrder->save();
                        self::notifyOperators($bankOrder);
                        continue;
                    }
                } else {
                    if ($bankOrder->canReCharge() && $bankOrder->try_count < self::MAX_TRY_COUNT) {
                        $bankOrder->status = self::STAT_NEW;
                        $bankOrder->save();
                    }
                    //used to log here...
                }
            } catch (\Exception $ex) {
                $bankOrder->status = self::STAT_FAILED;
                $bankOrder->save();
            }
        }
        return TRUE;
    }

    public static function notifyOperators($bankOrder)
    {
        $mailer = sfContext::getInstance()->getMailer();

        $from = 's.alarm@ntc.mn';
        $to = array('altantulga@mobicom.mn');
        $subject = '[Dealer alarm] ' . $bankOrder->order_id;
        $body = 'Сайн байна уу?<br><br>
                Шалтгаан: <b>' . self::getStatusName($bankOrder->status, BankCapitalTable::TYPE_DEALER) . '</b><br><br>
                Дараах цэнэглэлт амжилтгүй боллоо:<br>
                Гүйлгээний дугаар: <b>' . $bankOrder->order_id . '</b><br>
                Дансны дугаар: <b>' . $bankOrder->bank_account . '</b><br>
                Цэнэглэх дугаар: <b>' . $bankOrder->charge_mobile . '</b><br>
                Тушаасан дүн: <b>' . $bankOrder->charge_amount . '</b><br>
                Хуулганы огноо: <b>' . $bankOrder->order_date . '</b><br>';

        $message = Swift_Message::newInstance()
                ->setPriority(1)
                ->setFrom($from, 'Dealer Alarm')
                ->setSubject($subject)
                ->setBody($body, 'text/html')
                ->setTo($to);
        try {
            $res = $mailer->send($message);
        } catch (Swift_TransportException $e) {
            echo $e->getMessage();
        }
        LogTools::setLogEmail($subject, $body, $from, $to);
    }

