class BankCapitron extends BaseBankCapitron
{
    public function canReCharge()
    {
        if (in_array($this->status, array(
                    BankCapitronTable::STAT_FAILED,
                    BankCapitronTable::STAT_PROCESS,
                    BankCapitronTable::STAT_FAILED_CHARGE,
                    BankCapitronTable::STAT_FAILED_DEALER)) &&
                $this->try_count < BankCapitronTable::MAX_TRY_COUNT) {
            return TRUE;
        }
        return FALSE;
    }

    public function canReOutcome()
    {
        if (in_array($this->status, array(
                    BankCapitronTable::STAT_FAILED_OUTCOME
                ))) {
            return TRUE;
        }

        return FALSE;
    }
}
