    public static function init($date) {
        $transactions = CapitronCorpgw::getTransactions($date);
        if (count($transactions)) {
            if (!isset($transactions[0])) {
                $temp[0] = $transactions;
                $transactions = $temp;
            }
            foreach ($transactions as $trans) {
                if (BankCapitronTable::findOneByOrderId(trim($trans->jrno), $date)) {
                    continue;
                }
                $param = array();
                if ($trans->txntype == 'O') {
                    $param['TxnType'] = 'SUB';
                } else {
                    $param['TxnType'] = 'ADD';
                }
                $param['Account'] = $trans->acntno;
                $param['JournalNo'] = $trans->jrno;

                $param['TxnDesc'] = $trans->txndesc;
                $param['Amount'] = (double) $trans->amount;
                $param['Branch'] = 0;
                $param['TxnDate'] = date('Y-m-d H:i:s', strtotime(str_replace(' UTC', '', $trans->postdate)));

                try {
                    BankCapitronTable::insert($param);
                } catch (\Exception $exc) {
                    $logger = new sfFileLogger(new sfEventDispatcher(), array('file' => sfConfig::get('sf_log_dir') . '/my-capitron-order.log'));
                    $logger->log('--ERROR--=' . trim($param['JournalNo']) . $exc->getTraceAsString(), sfFileLogger::INFO);
                }
                unset($trans);
            }
        }
        return TRUE;
    }

    /**
     * Дахин цэнэглэлт
     *
     * @param null $bankOrder
     * @param int $limit
     * @return boolean
     * @throws Doctrine_Manager_Exception
     * @throws Doctrine_Query_Exception
     */
    public static function recharge($bankOrder = null, $limit = 50)
    {
        if ($bankOrder instanceof BankCapitron) {
            if ($bankOrder->canReCharge()) {
                $bankOrder->status = self::STAT_PROCESS;
                $bankOrder->save();
            }
            $bankOrderRows = array($bankOrder);
        } elseif ($bankOrder) {
            return FALSE;
        } else {
            $bankOrderRows = self::updateForCharge(array(BankCapitronAccountTable::ACCOUNT_DEALER, BankCapitronAccountTable::ACCOUNT_DEALER_MOBICOM), $limit);
        }

        foreach ($bankOrderRows as $bankOrder) {
            try {
                $SD = $ADshop = FALSE;
                $chargeResult = $adCode = null;
                if (in_array($bankOrder->status, array(self::STAT_FAILED_CHARGE, self::STAT_FAILED))) {
                    $bankOrder->status = self::STAT_PROCESS;
                    $bankOrder->save();
                }
                if ($bankOrder->status == self::STAT_PROCESS) {
                    $bankOrder->try_count++;
                    if ($bankOrder->charge_mobile != '') {
                        $dealer = DealerCharge::getDealer($bankOrder->charge_mobile);
                    } else {
                        $dealer = false;
                    }
                    if (BaseSms::isSdDealer($bankOrder->order_p)) {
                        $SD = TRUE;
                    } else {
                        $adCode = BaseSms::isAdShop($bankOrder->order_p);
                        if ($adCode) {
                            $ADshop = TRUE;
                        } else {
                            # If the charge amount exceeds the max amount limit
                            if ($bankOrder->order_amount > self::MAX_AMOUNT_LIMIT) {
                                $bankOrder->status = self::STAT_FAILED_MAX_AMOUNT;
                                $bankOrder->save();
                                self::notifyOperators($bankOrder);
                                continue;
                            } elseif ($bankOrder->order_amount < self::MIN_AMOUNT_LIMIT) {
                                $bankOrder->status = self::STAT_FAILED_MIN_AMOUNT;
                                $bankOrder->save();
                                self::notifyOperators($bankOrder);
                                continue;
                            }
                        }
                    }
                    if ($dealer || $adCode) {
                        $logId = LogTools::setLogCapitronCharge($bankOrder->id);

                        $time = -time();
                        # Цэнэглэлт хийх
                        if ($SD) {
                            $chargeResult = BaseSms::chargeSdDealer($bankOrder->charge_mobile, $bankOrder->order_amount, 'CAPITRON');
                        } elseif ($ADshop) {
                            $chargeResult = BaseSms::chargeAdDealer($adCode, $bankOrder->order_amount, 'CAPITRON');
                        } else {
                            $chargeResult = DealerCharge::charge($bankOrder->charge_mobile, $bankOrder->order_amount);
                        }
                        $time += time();

                        LogTools::updateLogCapitronCharge($logId, $chargeResult['log_request'], $chargeResult['log_response'], $time);

                        if ($chargeResult['success'] == TRUE) {
                            # Цэнэглэгдсэн мөнгөн дүн болон хувийг хадгалах
                            $bankOrder->charge_amount = $chargeResult['transferred'];
                            $bankOrder->percent = $chargeResult['percent'];
                            $bankOrder->save();

                            if ($SD || $ADshop) {
                                $bankOrder->status = self::STAT_SUCCESS;
                                $bankOrder->save();
                            } else {
                                # Зарлага үүсгэх
                                $outcomeOrderId = self::reoutcome($bankOrder, $dealer);
                                if ($outcomeOrderId) {
                                    $bankOrder->status = self::STAT_SUCCESS;
                                    $bankOrder->sales_order_id = $outcomeOrderId;
                                    $bankOrder->save();
                                    continue;
                                } else {
                                    $bankOrder->status = self::STAT_FAILED_OUTCOME;
                                    $bankOrder->save();
                                    self::notifyOperators($bankOrder);
                                    continue;
                                }
                            }
                        } else {
                            if ($chargeResult['error_code'] == self::STAT_FAILED_MAX_AMOUNT) {
                                $bankOrder->status = self::STAT_FAILED_MAX_AMOUNT;
                            } elseif ($chargeResult['error_code'] == self::STAT_FAILED_MIN_AMOUNT) {
                                $bankOrder->status = self::STAT_FAILED_MIN_AMOUNT;
                            } else {
                                $bankOrder->status = self::STAT_FAILED_CHARGE;
                            }
                            $bankOrder->save();
                            self::notifyOperators($bankOrder);
                            continue;
                        }
                    } else {
                        $bankOrder->status = self::STAT_FAILED_DEALER;
                        $bankOrder->save();
                        self::notifyOperators($bankOrder);
                        continue;
                    }
                } else {
                    if ($bankOrder->canReCharge() && $bankOrder->try_count < self::MAX_TRY_COUNT) {
                        $bankOrder->status = self::STAT_NEW;
                        $bankOrder->save();
                    }
                    //used to log here...
                }
            } catch (\Exception $ex) {
                $bankOrder->status = self::STAT_FAILED;
                $bankOrder->save();
            }
        }

        return TRUE;
    }

    /**
     * Дахин цэнэглэлт AD shop,SD
     *
     * @param $bankOrder
     * @param $type
     * @return boolean
     * @throws Doctrine_Manager_Exception
     */
    public static function rechargeSMSApi($bankOrder, $type)
    {
        if ($bankOrder instanceof BankCapitron) {
            if (!$bankOrder->canReCharge()) {
                return FALSE;
            }
        } else {
            return FALSE;
        }
        $chargeResult = null;
        $pdo = Doctrine_Manager::connection()->getDbh();
        $sql = "UPDATE bankgw.bank_capitron
              SET status = '" . self::STAT_PROCESS . "'
              WHERE id = '" . $bankOrder['id'] . "'
              LIMIT 1";
        $affectedRows = $pdo->exec($sql);
        if ($affectedRows == 0) {
            return FALSE;
        }
        $bankOrder->status = self::STAT_PROCESS;
        $bankOrder->try_count++;

        # Цэнэглэлт хийх
        $logId = LogTools::setLogCapitronCharge($bankOrder->id);
        $time = -time();
        if ($type == "SD") {
            $chargeResult = BaseSms::chargeSdDealer(str_replace("SD", "", $bankOrder->charge_mobile), $bankOrder->order_amount, 'CAPITRON');
        } elseif ($type == "AD") {
            $chargeResult = BaseSms::chargeAdDealer($bankOrder->charge_mobile, $bankOrder->order_amount, 'CAPITRON');
        }
        $time += time();
        LogTools::updateLogCapitronCharge($logId, $chargeResult['log_request'], $chargeResult['log_response'], $time);

        if ($chargeResult['success'] == TRUE) {
            # Цэнэглэгдсэн мөнгөн дүн хадгалах
            $bankOrder->charge_amount = $bankOrder->order_amount;
            $bankOrder->status = self::STAT_SUCCESS;
            $bankOrder->save();
            return TRUE;
        } else {
            if ($chargeResult['error_code'] == self::STAT_FAILED_MAX_AMOUNT) {
                $bankOrder->status = self::STAT_FAILED_MAX_AMOUNT;
            } elseif ($chargeResult['error_code'] == self::STAT_FAILED_MIN_AMOUNT) {
                $bankOrder->status = self::STAT_FAILED_MIN_AMOUNT;
            } else {
                $bankOrder->status = self::STAT_FAILED_CHARGE;
            }
            $bankOrder->save();
        }
        return false;
    }

