        }
        $logger->log('Total count: ' . count($bankOrderRows), sfFileLogger::INFO);
        $count = 0;
        echo 'bankPaymentHBB count =>'.count($bankOrderRows).'</br>';
        if(count($bankOrderRows)>0){
        foreach ($bankOrderRows as $bankOrder) {
