<?php# http://bankgw.local/mobinet/InquiryRequest.php?contract=BGN-001include("classes/Mobinet.php");include("classes/InquiryRequest.php");$requestId = (int) isset($_REQUEST['request_id']) ? $_REQUEST['request_id'] : 0;$userId = (int) isset($_REQUEST['user_id']) ? $_REQUEST['user_id'] : 0;$contract = isset($_REQUEST['contract']) ? $_REQUEST['contract'] : "";$bundle = isset($_REQUEST['bundle']) ? $_REQUEST['bundle'] : "";$speed = isset($_REQUEST['speed']) ? $_REQUEST['speed'] : "";$month = isset($_REQUEST['month']) ? $_REQUEST['month'] : "";$staff = isset($_REQUEST['staff']) ? $_REQUEST['staff'] : "";/** * Create */if (($contract == "")) {    header('Content-Type: text/xml');    $xmler = <<<XML<?xml version='1.0' encoding='UTF-8'?><InquiryRequestRes><Code>1004</Code><Info>Invalid CONTRACT</Info></InquiryRequestRes>XML;    echo $xmler;    die();}$caller = new InquiryRequest();$caller->setUserId($userId);$caller->setXml($requestId, $contract, $bundle, $speed, $month, $staff);$caller->call($caller->getXmlRequest(), $caller->url);header('Content-Type: text/xml');if ($caller->getXmlResponseRaw() == "") {    echo $caller->responseIsEmpty();} else {    echo $caller->getXmlResponseRaw();}?>